import { Directive, ElementRef, HostListener, Renderer2 } from '@angular/core';

@Directive({
  selector: '[appScrollControlledText]'
})
export class ScrollControlledTextDirective {
  constructor(private el: ElementRef, private renderer: Renderer2) {}

  @HostListener('window:scroll', [])
  onWindowScroll(): void {
    const scrollY = window.scrollY; // Lấy vị trí thanh cuộn theo trục Y
    const start = 1480; // Vị trí bắt đầu
    const end = 1800;   // Vị trí dừng

      if (scrollY >= start && scrollY <= end) {
        
        const progress = (scrollY - start) / (end - start); // Giá trị từ 0 đến 1
        const movePercent = progress * 100; // Tương ứng với phần trăm
      
        this.renderer.setStyle(
          this.el.nativeElement,
          'transform',
          `translateX(-${movePercent}%)`
        );
      }
  }
}
